def SequentialSearch(a, n, key):
    i = 0
    while i < n and a[i] != key:
        i += 1
    if i < n:
        return i
    else:
        return -1

def SequentialSearch(a, n, key):
    i = 0
    i += 1
    while i < n and a[i] < key:
        i += 1
    if i < n and a[i] = key:
        return i
    else:
        return -1

