p = 'is'	# 찾을 패턴
t = 'This is a book~!'	# 전체 텍스트
m = len(p)
n = len(t)

def BruteForce(p, t):
    i, j = 0, 0
    while j < m and i < n:
        if t[i] != p[j]:
            i = i - j
            j = -1
        i = i + 1
        j = j + 1
    if j == m:
        return i - m
    else:
        return -1
